import React, { useEffect, useState } from "react";
import { uniqueId } from "lodash";
const Cards = (props) => {

    const displayedOrders = props.displayedOrders

    const [count, setCount] = useState(0)
    const [clickSequence, setClickSequence] = useState([])

    const sequence = []

    const saveNumber = (divContent) => {
        console.log(divContent)
        console.log(sequence)
        if(clickSequence.indexOf(divContent) === -1) {
            sequence.unshift(divContent)
            setClickSequence(sequence)
            setCount(count + 1)
            console.log('een')
        } else {
            alert('game over')
            setClickSequence([])
            setCount(0)
            console.log('twee')
        }
    }



    function GenerateCards () {
        let divs = displayedOrders[count].map((element) => {
            return <div key={uniqueId()} className="innerCards" onClick={() => saveNumber(element)}>{element}</div>
        })
        return <div className="cards">{divs}</div>
    }


    return (
        <div >
            <GenerateCards/>
            <div>{clickSequence}</div>
        </div>
    )
}

export default Cards;
