import React, { useEffect } from "react";
const Cards = (props) => {
    const displayedOrders = props.displayedOrders
    console.log(displayedOrders)
    let clickSequence = []
    let comboCounter = 0

    const saveNumber = (divContent) => {
        if(clickSequence.indexOf(divContent) === -1) {
            clickSequence.push(divContent)
            comboCounter++
        } else {
            alert('game over')
            clickSequence = []
        }
    }


    function GenerateCards () {
        let divs = displayedOrders[comboCounter].map((element) => {
            return <div className="innerCards" onClick={() => saveNumber(element)}>{element}</div>
        })
        return <div className="cards">{divs}</div>
    }


    return (
        <div >
            <GenerateCards/>
            <div>{displayedOrders}</div>
        </div>
    )
}

export default Cards;
