import React, { useEffect, useState } from "react";
const Cards = (props) => {

    const displayedOrders = props.displayedOrders

    const [count, setCount] = useState(0)
    const [clickSequence, setClickSequence] = useState([''])


    console.log(clickSequence)

    const saveNumber = (divContent) => {
        if(clickSequence.indexOf(divContent) === -1) {
            setClickSequence(...clickSequence, clickSequence.push(divContent))
            setCount(count + 1)
        } else {
            alert('game over')
            setClickSequence([])
            setCount(0)
        }
    }



    function GenerateCards () {
        let divs = displayedOrders[count].map((element) => {
            return <div className="innerCards" onClick={() => saveNumber(element)}>{element}</div>
        })
        return <div className="cards">{divs}</div>
    }


    return (
        <div >
            <GenerateCards/>
            <div>{clickSequence}</div>
        </div>
    )
}

export default Cards;
