import React, { useState, useEffect } from "react";

const App = () => {


  const memoryCards = ['apple', 'banana', 'orange', 'kiwi', 'pear']
  const displayedOrders = []

  const StoreDisplayOrder = (recursions) => { // no repetition allowed
    let newDisplayOrder = []

    for(let i = 0; i < 5; i++) {
      newDisplayOrder.push(Number.parseInt(Math.random() * 5))
    }
    if(displayedOrders[0] === undefined) {
      displayedOrders.push(newDisplayOrder)
      return
    }
    for(const i in displayedOrders) {
      if(displayedOrders[i] === newDisplayOrder) {
        return StoreDisplayOrder
      } else {
        displayedOrders.push(newDisplayOrder)
        return StoreDisplayOrder
      }
    }
  }

  StoreDisplayOrder()
  console.log(displayedOrders)

  return (
    <div>
      {displayedOrders}
    </div>
  )
};

export default App;
